#include <iostream>
using namespace std;
int main(){
    cout<<"(5&7)"<<(5&7)<<endl;//bitwise opt
    cout<<"(5&2)"<<(5&2)<<endl;
    cout<<"(5|10)"<<(5|10)<<endl;//bitwise opt
    cout<<"(5|2)"<<(5|2)<<endl;
     cout<<"(5^7)"<<(5^7)<<endl;//bitwise opt
    cout<<"(5^2)"<<(5^2)<<endl;
    return 0;
}